import { Button, Image, Modal, notification, Spin } from 'antd';
import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useStoreContext } from '../../../context/Store';
import { Favourite } from '../../../types';
import { DeleteOutlined } from '@ant-design/icons';
import { getFavourite, removeFavourite } from '../../../api/favourites';

const FavouritesModal: React.FC = () => {
  const { getStoreFavourite, deleteStoreFavourite } = useStoreContext();
  const [favourite, setFavourite] = React.useState<Favourite | undefined>();
  const [favouriteLoading, setFavouriteLoading] =
    React.useState<boolean>(false);
  const [favouriteButtonLoading, setFavouriteButtonLoading] =
    React.useState<boolean>(false);

  const emptyFavourite =
    'data:Favourite/png;base64,';

  const navigate = useNavigate();
  const { id: favouriteId } = useParams<'id'>();

  React.useEffect(() => {
    if (favouriteId) {
      const favouriteFromStore = getStoreFavourite(+favouriteId);
      if (favouriteFromStore) {
        setFavourite(favouriteFromStore);
      } else {
        setFavouriteLoading(true);
        getFavourite(+favouriteId)
          .then((res) => {
            setFavourite(res);
          })
          .catch((e) => {
            notification.error({
              message: e.message,
              description: e.response?.data.message || ''
            });
          })
          .finally(() => {
            setFavouriteLoading(false);
          });
      }
    }
  }, []);

  const onClose = () => {
    navigate('/favourites');
  };

  const onRemove = () => {
    if (favouriteId) {
      setFavouriteButtonLoading(true);
      removeFavourite(+favouriteId)
        .then(() => {
          deleteStoreFavourite(+favouriteId);
          notification.success({ message: 'Successfully removed' });
        })
        .catch((e) => {
          notification.error({
            message: e.message,
            description: e.response?.data.message || ''
          });
        })
        .finally(() => {
          onClose();
          setFavouriteButtonLoading(false);
        });
    }
  };

  return (
    <Modal
      className='favourite-modal'
      visible
      onCancel={onClose}
      destroyOnClose
      width={620}
      title={`Favourite ID is '${favouriteId}'`}
      footer={[
        <Button key='close' onClick={onClose}>
          Close
        </Button>,
        <Button
          key='remove'
          loading={favouriteButtonLoading}
          onClick={onRemove}
          icon={<DeleteOutlined />}
          danger
          type='primary'>
          Remove from Favourites
        </Button>
      ]}>
      <div
        style={{
          display: 'flex',
          flexDirection: 'column',
          position: 'relative',
          alignItems: 'center'
        }}>
        {!favouriteLoading ? (
          <>
            <Image
              src={favourite ? favourite.image.url : emptyFavourite}
              fallback={emptyFavourite}
            />
          </>
        ) : (
          <Spin size='large' />
        )}
      </div>
    </Modal>
  );
};

export { FavouritesModal };
